-module(ecc_oem).-export([oem/1,oem_footer_logo/0,oem_footer_1/0,oem_info/2]).oem(Txt)->	OemData = get_oem_data(),	case OemData of		{ok,Data}->			case proplists:get_value(oem,Data) of				true->					oem_replace(Txt,proplists:get_value(replace,Data));				_->					Txt			end;		_->			Txt	end.	oem_replace(Txt,[])->Txt;oem_replace(Txt,[{O,R}|T])->	 oem_replace(replace(Txt,O,R),T);oem_replace(Txt,[_|T])->	oem_replace(Txt,T);oem_replace(Txt,_)->Txt.replace(T,O,R)->	case string:str(T,O) of		0->			T;		P->			NT = string:left(T,P-1) ++ R ++ string:substr(T,P+length(O)),			replace(NT,O,R)	end.	oem_footer_logo()->	oem_info(footer_logo,"/images/logo.gif").	oem_info(K,Default)->	OemData = get_oem_data(),	case OemData of		{ok,Data}->			case proplists:get_value(oem,Data) of				true->					proplists:get_value(K,Data);				_->					Default			end;		_->			Default	end.oem_footer_1()->	oem_info(footer_1,"@siteview.com 2001-~s , All rights reserved.").		get_oem_data()->	case get('_oem_data_') of		undefined->			Data = file:consult("conf/oem.conf"),			put('_oem_data_',Data),			Data;		V->			V	end.			