-module(erlcmdb).-include("erlcmdb.hrl").-compile(export_all).start()->erlcmdb_template:start().	stop()->erlcmdb_template:stop().uuid()->dbcs_base:uuid().create_ci(Type,Data)->erlcmdb_ci:create(Type,Data).update_ci(Type,Id,Data)->erlcmdb_ci:update(Type,Id,Data).find_ci(Id)when is_atom(Id)->erlcmdb_ci:find_by_id(Id);find_ci(Where)->erlcmdb_ci:find(Where).find_ci(Type,Id)when is_atom(Id)->erlcmdb_ci:find_by_id(Type,Id);find_ci(Type,Where)->erlcmdb_ci:find(Type,Where).find_ci(Type,Where,Order)->erlcmdb_ci:find(Type,Where,Order).find_ci(Type,Where,From,Count)->erlcmdb_ci:find(Type,Where,From,Count).find_ci(Type,Where,From,Count,Order)->erlcmdb_ci:find(Type,Where,From,Count,Order).find_ci1(Where)->erlcmdb_ci:find1(Where).delete_ci(Type,Id)->erlcmdb_ci:delete(Type,Id).get_all_template()->erlcmdb_template:get_all_template().get_template(Type)->erlcmdb_template:get_template(Type).get_root()->erlcmdb_template:get_root_template().get_child_template(Type)->erlcmdb_template:get_child_template(Type).